import nprogress from &#39;nprogress&#39;
import &#39;nprogress/nprogress.css&#39;
const request = axios.create(&#123;
  baseURL: &#39;/api&#39;,
  timeout: 5000
&#125;)

request.interceptors.request.use((config) =&gt; &#123;
  nprogress.start()
  return config
&#125;)

request.interceptors.response.use((res) =&gt; &#123;
  nprogress.done()
  return res.data
&#125;, (error) =&gt; &#123;
  return Promise.reject(error)
&#125;)

